.button {
  background-color: #ffffff; 
  border: solid;
  color: black;
  padding: 16px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  margin: 4px 2px;
  transition-duration: 0.4s;
  cursor: pointer;
}

.button:hover {
  background-color: #4CAF50; 
  color: white;
}

.input[type=text] {
  width: 60%;
  padding: 15px 20px;
  margin: 8px 0;
  box-sizing: border-box;
  border: 1px solid #555;
  outline: none;
}

.input[type=text]:focus {
  border: 3px solid #555;
}
