Codigo.Codigo:
var nombres = ['Juan', 'Pedro', 'Ana', 'Luisa', 'María'];
		// Cargar los nombres en la tabla
		var tablaNombres = document.getElementById('tabla-nombres');
		for (var i = 0; i < nombres.length; i++) {
			var fila = document.createElement('tr');
			var celda = document.createElement('td');
			var textoCelda = document.createTextNode(nombres[i]);
			celda.appendChild(textoCelda);
			fila.appendChild(celda);
			tablaNombres.appendChild(fila);
		}

		function buscarNombre() {
			var nombreBuscado = document.getElementById('nombre').value;
			if (nombres.includes(nombreBuscado)) {
				alert(nombreBuscado + " se encuentra en la lista de nombres.");
			} else {
				alert(nombreBuscado + " no se encuentra en la lista de nombres.");
			}
		}
