// Define una clase Persona con nombre, apellidos y edad
class Persona {
  constructor(nombre, apellidos, edad) {
    this.nombre = nombre;
    this.apellidos = apellidos;
    this.edad = edad;
  }
}

// Crea una colección de objetos Persona
const personas = [
  new Persona("Maria", "Garcia López", 32),
  new Persona("Pau", "Martínez González", 27),
  new Persona("Laura", "González Sánchez", 42),
  new Persona("Jordi", "Torres Rodríguez", 19),
  new Persona("Eva", "Garcia Pérez", 23),
  new Persona("Marc", "Martínez García", 38)
];

// Función para mostrar la colección de objetos Persona en una tabla HTML
function mostrarPersonas() {
  const tbody = document.querySelector('#people-table-body');
  tbody.innerHTML = '';
  personas.forEach((persona) => {
    const tr = document.createElement('tr');
    tr.innerHTML = `
      <td>${persona.nombre}</td>
      <td>${persona.apellidos}</td>
      <td>${persona.edad}</td>
    `;
    tbody.appendChild(tr);
  });
  // Mostrar la tabla después de que se haya actualizado con los datos
  const table = document.querySelector('table');
  table.classList.remove('hidden');
}

// Función para ordenar la colección de objetos Persona por edad
function ordenarPorEdad(ascendente) {
  personas.sort((persona1, persona2) => {
    if (ascendente) {
      return persona1.edad - persona2.edad;
    } else {
      return persona2.edad - persona1.edad;
    }
  });
  mostrarPersonas();
}

// Añadir un evento al botón para ordenar ascendentemente
const sortAscButton = document.querySelector('#sort-asc-btn');
sortAscButton.addEventListener('click', () => {
  ordenarPorEdad(true);
});

// Añadir un evento al botón para ordenar descendentemente
const sortDescButton = document.querySelector('#sort-desc-btn');
sortDescButton.addEventListener('click', () => {
  ordenarPorEdad(false);
});

// Mostrar la lista de personas desordenada al principio
mostrarPersonas();

