function setOperatorCalculadora(a) {
  var Numero1 = document.getElementById("primernumero").value;
  var Numero2 = document.getElementById("segonnumero").value;
  var resultat;
  var resultat2;
  switch (a) {
    //Suma
    case 1:
      resultat = (parseFloat(Numero1) + parseFloat(Numero2));
      break;
      //Resta
    case 2:
      resultat = (Numero1 - Numero2);
      break;
      //Multiplicacio
    case 3:
      resultat = (Numero1 * Numero2);
      break;
      //Divisio
    case 4:
      resultat = (Numero1 / Numero2);
      break;
      //Modul
    case 5:
      resultat = (Numero1 % Numero2);
      break;

  }
  document.getElementById("respuesta").innerHTML = resultat;
}

function OperadoresLog(a) {
var Numero1 = document.getElementById("primernumero2").value;
var Numero2 = document.getElementById("segonnumero2").value;
  switch (a) {
    //==
    case 1:
      resultat2 = (Numero1 == Numero2);      
      break;
      //===
    case 2:
      resultat2 = (Numero1 === Numero2);
      break;
      //<
    case 3:
      resultat2 = (Numero1 < Numero2);
      break;
      //>
    case 4:
      resultat2 = (Numero1 > Numero2);
      break;
      //!=
    case 5:
      resultat2 = (Numero1 != Numero2);
      break;

  }
  document.getElementById("respuesta2").innerHTML = resultat2;
}

