function obtenerDia() {
  let dia = document.getElementById("dia").value;
  let resultado = document.getElementById("resultado");

  switch(dia) {
    case "1":
      resultado.innerHTML = "Lunes";
      break;
    case "2":
      resultado.innerHTML = "Martes";
      break;
    case "3":
      resultado.innerHTML = "Miércoles";
      break;
    case "4":
      resultado.innerHTML = "Jueves";
      break;
    case "5":
      resultado.innerHTML = "Viernes";
      break;
    case "6":
      resultado.innerHTML = "Sábado";
      break;
    case "7":
      resultado.innerHTML = "Domingo";
      break;
    default:
      resultado.innerHTML = "Entrada inválida. Por favor ingresa un número entre 1 y 7.";
      break;
  }
}

body {
  font-family: Arial, sans-serif;
  background-color: #f2f2f2;
}

#contenedor {
  max-width: 600px;
  margin: 0 auto;
  text-align: center;
}

h1 {
  color: #333;
}

label {
  display: block;
  margin-bottom: 10px;
}

input[type="number"] {
  padding: 5px;
  border-radius: 5px;
  border: 1px solid #ccc;
  margin-bottom: 10px;
}

button {
  background-color: #4CAF50;
  color: #fff;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-bottom: 10px;
}

button:hover {
  background-color: #3e8e41;
}

#resultado {
  font-size: 20px;
  font-weight: bold;
  color: #333;
}
