const calcularEdad = () => {
  const fechaNacimiento = new Date(document.getElementById("fechaNacimiento").value);
  const hoy = new Date();
  const tiempoTranscurrido = hoy - fechaNacimiento;

  const anios = Math.floor(tiempoTranscurrido / 31536000000);
  const meses = Math.floor((tiempoTranscurrido % 31536000000) / 2628000000);
  const dias = Math.floor(((tiempoTranscurrido % 31536000000) % 2628000000) / 86400000);

  document.getElementById("resultado").textContent = `Tienes ${anios} años, ${meses} meses y ${dias} días.`;
};

document.getElementById("calcularEdad").addEventListener("click", calcularEdad);

