function myFunc() {

  let Producte = CrearProducte();  
  let Fila = document.getElementById('intento').insertRow(-1);

  let col1 = Fila.insertCell(0);
  let col2 = Fila.insertCell(1);
  let col3 = Fila.insertCell(2);
  let col4 = Fila.insertCell(3);

  col1.textContent = Producte.Id;
  col2.textContent = Producte.Name;
  col3.textContent = Producte.Price;
  col4.textContent = Producte.Quantity;

}

function CrearProducte()
{
	let inputId = document.getElementById("productid").value;
  let inputName = document.getElementById("productname").value;
  let inputPrice= document.getElementById("productprice").value;
  let inputQuant= document.getElementById("productquant").value;
  var product = {
    Id			: inputId,
    Name 		: inputName,
    Price		: inputPrice,
    Quantity: inputQuant   
  };
return product;
}

