function WindowAlert()
{
window.alert("Estigues alerta");
}
function WindowPrompt()
{
window.prompt("Escriu Algo");
}
function WindowConfirm()
{
window.confirm("Confirm?");
}
function WindowAuto()
{
let out;
do
{
	out = true;
  let prompt = window.prompt("Escriu Algo");
  var confirma = window.confirm("Segur que vols escriure "+prompt+"?");
  if(confirma)
  {
    window.alert("Segur segur segur?");
    out = false;
  }

}while(out);

}
