const textbox = document.forms["myForm"]["textbox"];
const radio = document.forms["myForm"]["lista_opciones"];
const checkvalues = document.forms["myForm"]["opciones[]"];
let selectedanimals = [];

function ResumeDataToString() {
  alert("Datos introducidos correctamente");
  alert("Nombre: " + textbox.value + "\n" +
    "Edad: " + radio.value + "\n" +
    "Animales: " + selectedanimals.toString()
  );
}

function work() {
  let Sumit = ChekearAnimales() && ChekearTextBox();
  if (Sumit) {
    ResumeDataToString();
  }
  return false;
}

function ChekearTextBox() {
  if (textbox.value.charAt(0) == textbox.value.charAt(0).toLowerCase()) {
    alert("La primera letra ha de ser mayuscula");
  }
  return textbox.value.charAt(0) != textbox.value.charAt(0).toLowerCase();
}

function ChekearAnimales() {
  var lista = PillarAnimales();
  if (lista.length <= 2 && lista.length > 0) {
    console.log(lista.toString());
    selectedanimals = lista;
  } else {
    alert("Tienes que seleccionar 1 o 2 animales");
  }
  return lista.length <= 2 && lista.length > 0;
}

function PillarAnimales() {
  let animales = [];
  let cntanimales = 0;
  let cntanimalesdefinitivo = 0;
  checkvalues.forEach(element => {
    if (element.checked) {
      animales.push(element.value);
    }
  });
  return animales;
}

input:invalid {
  border: 2px dashed red;
}

input:valid {
  border: 2px solid black;
}
